<?php/** * The template for displaying search results pages. * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/#search-result * * @package Visanow */get_header(); ?><section class="lightgrey-bg">	<div class="privacy-statement white-bg uk-container uk-container-center">		<div class="search-header borderdown">			<div class="uk-grid">				<div class="uk-width-1-6">					<h4>Search</h4>				</div>				<div class="uk-width-5-6">					<div class="uk-float-right"><?php echo do_shortcode('[facetwp counts="true"]'); ?></div>				</div>			</div>		</div>		<div class="search-content">			<div class="uk-grid">				<div class="uk-width-3-10">					<div class="search-sidebar">						<h6>FILTER BY</h6>						<div class="custom-checkbox selected loading">All</div>						<?php echo facetwp_display( 'facet', 'post_types' ); ?>						<?php echo facetwp_display( 'facet', 'post_parent' ); ?>					</div>					<div class="search-sidebar no-border">						<h6>RELATED TOPICS</h6>						<?php wp_nav_menu( array( 'theme_location' => 'related-topic', 'menu_id' => 'related-topic', 'container' => false ) ); ?>											</div>				</div>				<div class="uk-width-7-10 facetwp-template">             					<?php if ( have_posts() ) : ?>						<!-- .page-header -->						<?php while ( have_posts() ) : the_post(); ?>							<?php get_template_part( 'template-parts/content', 'search' ); ?>						<?php endwhile; ?>						<div class="search_navigation"><?php //wp_pagenavi(); ?><?php echo facetwp_display( 'pager' ); ?></div>					<?php else : ?>						<?php get_template_part( 'template-parts/content', 'none' ); ?>					<?php endif; ?>					</div>													</div>			</div>		</div>	</div>	</div></section>	<?phpget_footer();