<?php/** * Template part for displaying posts. * * @link https://codex.wordpress.org/Template_Hierarchy * * @package Visanow */?>	<div id="post-<?php the_ID(); ?>" class="directors">                    <div class="uk-grid">                        <div class="uk-width-medium-2-10">                        	<div class="director-image uk-border-circle">                            	<?php the_post_thumbnail(  array( 104,104 ) ); ?>                            </div>                                                    </div>                        <div class="uk-width-medium-5-10">                        	<h5><?php the_title();?></h5>                            <span><?php the_field('member_position');?></span>                            <div class="member_info"><?php the_content();?></div>							<a class="more_info" href="#">READ MORE</a>                        </div>																		<?php $choose = get_field('member_option');?>						                        <div class="uk-width-medium-3-10">                        	<div class="related uk-text-center">							<?php if ($choose == 'Image'): ?>								<span><?php the_field('image_lebel');?></span>                            	<img src="<?php the_field ('image_quote');?>" alt="">							<?php else: ?>																															 <img class="quote-image" src="<?php echo get_template_directory_uri(); ?>/images/quote1.png" alt="">                              	 <p><?php the_field('member_quote');?></p>                                 <img class="quote-image" src="<?php echo get_template_directory_uri(); ?>/images/quote2.png" alt="">							<?php endif;?>                                                            </div>                        </div>										   </div>               </div>