get_field ('icon');?>					<img alt="" class="uk-float-right" src="<?php echo $post_icon;?>" data-uk-svg>                        				</div>				<div class="box-content">					<div class="fix-height uk-text-left">						<h6><?php the_title();?></h6>						<span><?php the_time('M,j,Y');?>  | </span>						<span><?php the_author();?></span>						<span>1:00 pm EST</span>						<?php $trimexcerpt = get_the_excerpt();?>						<?php $shortexcerpt = wp_trim_words( $trimexcerpt, $num_words = 17, $more = 'â€¦ ' );?>						<?php echo '<p>' . $shortexcerpt . '</p>';?>                 						</div>					<?php $btn_text = get_field('button_text'); ?>					<a class="read-more uk-border-rounded" href="<?php the_permalink(); ?>">					<?php if($btn_text): ?><?php echo $btn_text; ?><?php else: ?>Read More<?php endif; ?>				</a>				</div>			</div>					<?php endwhile; ?>		<?php endif; wp_reset_query(); ?>		</div>	</div>             </section>	<script type="text/javascript">jQuery(document).ready(function($) {	//isotop//  var $container = $('#container'),      $checkboxes = $('#filters input');    $container.isotope({    itemSelector: '.item',	 percentPosition: true,	  masonry: {		columnWidth: '.grid-sizer'	  }  });    $checkboxes.change(function(){    var filters = [];    // get checked checkboxes values    $checkboxes.filter(':checked').each(function(){      filters.push( this.value );    });    // ['.red', '.blue'] -> '.red, .blue'    filters = filters.join(', ');    $container.isotope({ filter: filters });  });      $('#shuffle').click(function(){    $container.isotope('shuffle');  });  				//**************************** // Isotope Load more button //**************************** var initShow = 4; //number of items loaded on init & onclick load more button var counter = initShow; //counter for load more button var iso = $container.data('isotope'); // get Isotope instance loadMore(initShow); //execute function onload function loadMore(toShow) {   $container.find(".hidden").removeClass("hidden");   var hiddenElems = iso.filteredItems.slice(toShow, iso.filteredItems.length).map(function(item) {     return item.element;   });   $(hiddenElems).addClass('hidden');   $container.isotope('layout');   //when no more to load, hide show more button   if (hiddenElems.length == 0) {     jQuery("#load-more").hide();   } else {     jQuery("#load-more").show();   }; } //append load more button $container.after('<span id="load-more" class="learn-more uk-border-rounded">Load More</span>'); //when load more button clicked $("#load-more").click(function() {   if ($('#filters input').data('clicked')) {     //when filter button clicked, set initial value for counter     counter = initShow;     $('#filters input').data('clicked', false);   } else {     counter = counter;   };   counter = counter + initShow;   loadMore(counter); }); //when filter button clicked $("#filters").click(function() {   $(this).data('clicked', true);   loadMore(initShow); });	});</script>		<?phpget_footer();